import React, { useContext } from 'react';
import { ContextGlobal } from './utils/global.context'; // Asegúrate de importar correctamente tu contexto global

const Footer = () => {
  const { state } = useContext(ContextGlobal); // Obtiene el estado del contexto global

  // Determina la clase a aplicar dependiendo del tema
  const footerClass = state.theme === 'dark' ? 'footer dark' : 'footer light';

  return (
    <footer className={footerClass}>
      <div className="franja-roja container-block">
        <p className="texto">Odonto House</p>
        <p>Powered by</p>
        <img src="/images/DH.png" alt='DH-logo' />
      </div>

      <div className="icono-central container-block">
      
      </div>

      <div className='iconos-redes-sociales'>
        <img src="/images/ico-facebook.png" alt="Facebook" />
        <img src="/images/ico-instagram.png" alt="Instagram" />
        <img src="/images/ico-whatsapp.png" alt="Whatsapp" />
        <img src="/images/ico-tiktok.png" alt="TikTok" />
      </div>
    </footer>
  );
};

export default Footer;
