import React, { useState } from "react";


const Form = ({ onFormSubmit }) => {
  const [nombre, setNombre] = useState('');
  const [email, setEmail] = useState('');
  const [nombreError, setNombreError] = useState('');
  const [emailError, setEmailError] = useState('');

  const handleNombreChange = event => {
    const nuevoNombre = event.target.value;
    setNombre(nuevoNombre);

    if (nuevoNombre.length > 5) {
      setNombreError('');
    } else {
      setNombreError('El nombre del usuario debe tener como minimo 5 letras');
    }
  };

  const handleEmailChange = event => {
    const nuevoEmail = event.target.value;
    setEmail(nuevoEmail);

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (emailRegex.test(nuevoEmail)) {
      setEmailError('');
    } else {
      setEmailError('Ingrese un email válido.');
    }
  };

  const handleSubmit = event => {
    event.preventDefault();

    if (!nombreError && !emailError) {
      console.log('Nombre:', nombre);
      console.log('Email:', email);
      onFormSubmit({ nombre, email });
    } else {
      console.log('Algunos de los datos son incorrectos, reviselos y vuelva a enviar el formulario');
    }
  };

  return (
    <div>
      <form className="form" onSubmit={handleSubmit}>
          
            <input
              placeholder="Full name"
              type="text"
              id="nombre"
              value={nombre}
              onChange={handleNombreChange}
            />
            {nombreError && <p className="error">{nombreError}</p>}
          
          
            <input
              placeholder="Email"
              type="text"
              id="email"
              value={email}
              onChange={handleEmailChange}
            />
            {emailError && <p className="error">{emailError}</p>}
          
          <button type="submit">Send</button>
        </form>
    </div>
  );
};

export default Form;
